        from_user_id,
        recipient_id,
        message,
        image_id
    ) VALUES (
        v_from_user_id,
        p_recipient_id,
        p_message,
        p_image_id
    ) RETURNING id INTO v_message_id;
    
    RETURN v_message_id;
END;
