CREATE OR REPLACE FUNCTION "public"."complete_user_registration"("p_first_name" "text" DEFAULT NULL::"text", "p_last_name" "text" DEFAULT NULL::"text", "p_role" "text" DEFAULT 'user'::"text") RETURNS "json"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'pg_catalog', 'public'
    AS $$
DECLARE
    v_auth_user_id uuid;
    v_user_email text;
    v_user_id uuid;
BEGIN
    -- Get the current auth user
    v_auth_user_id := auth.uid();
    
    IF v_auth_user_id IS NULL THEN
        RETURN json_build_object(
            'success', false,
            'error', 'Not authenticated'
        );
    END IF;
    
    -- Get email from auth.users
    SELECT email INTO v_user_email
    FROM auth.users
    WHERE id = v_auth_user_id;
    
    -- Check if user already exists in public.users
    SELECT id INTO v_user_id
    FROM public.users
    WHERE auth_id = v_auth_user_id OR email = v_user_email;
    
    IF v_user_id IS NOT NULL THEN
        -- Update existing user
        UPDATE public.users
        SET 
            auth_id = v_auth_user_id,
            first_name = COALESCE(p_first_name, first_name),
            last_name = COALESCE(p_last_name, last_name),
            updated_at = now()
        WHERE id = v_user_id;
    ELSE
        -- Create new user
        INSERT INTO public.users (
            id,
            auth_id,
            email,
            first_name,
            last_name,
            role,
            status,
            created_at,
            updated_at
        ) VALUES (
            gen_random_uuid(),
            v_auth_user_id,
            v_user_email,
            p_first_name,
            p_last_name,
            p_role,
            'active',
            now(),
            now()
        )
        RETURNING id INTO v_user_id;
    END IF;
    
    RETURN json_build_object(
        'success', true,
        'user_id', v_user_id,
        'message', 'User registration completed successfully'
    );
END;
$$;


ALTER FUNCTION "public"."complete_user_registration"("p_first_name" "text", "p_last_name" "text", "p_role" "text") OWNER TO "postgres";


