CREATE OR REPLACE FUNCTION "public"."create_dj_contest_event"("p_dj_id" "uuid", "p_location_id" "uuid", "p_title" "text", "p_description" "text", "p_contestant_count" integer, "p_event_config" "jsonb" DEFAULT '{}'::"jsonb") RETURNS "uuid"
    LANGUAGE "plpgsql"
    SET "search_path" TO 'pg_catalog', 'public'
    AS $$
DECLARE
    v_event_id UUID;
BEGIN
    -- Create the event
    INSERT INTO dj_events (
        dj_id,
        location_id,
        event_type,
        title,
        description,
        status,
        event_config,
        created_at
    ) VALUES (
        p_dj_id,
        p_location_id,
        'contest',
        p_title,
        p_description,
        'pending',
        p_event_config,
        NOW()
    ) RETURNING id INTO v_event_id;
    
    -- Create placeholder contestants
    FOR i IN 1..p_contestant_count LOOP
        INSERT INTO dj_contestants (
            event_id,
            contestant_number,
            contestant_name,
            created_by
        ) VALUES (
            v_event_id,
            i,
            'Contestant ' || i,
            p_dj_id
        );
    END LOOP;
    
    RETURN v_event_id;
END;
$$;


