CREATE OR REPLACE FUNCTION "public"."create_pack_post"("p_pack_id" "uuid", "p_content" "text", "p_post_type" "text" DEFAULT 'message'::"text", "p_media_urls" "text"[] DEFAULT NULL::"text"[]) RETURNS "jsonb"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'pg_catalog', 'public'
    AS $$
DECLARE
    v_user_id UUID;
    v_video_id UUID;
    v_membership RECORD;
BEGIN
    v_user_id := auth.uid();
    
    IF v_user_id IS NULL THEN
        RETURN jsonb_build_object('success', false, 'error', 'Not authenticated');
    END IF;
    
    -- Check membership
    SELECT * INTO v_membership
    FROM pack_members
    WHERE pack_id = p_pack_id 
    AND user_id = v_user_id 
    AND is_active = true;
    
    IF NOT FOUND THEN
        RETURN jsonb_build_object('success', false, 'error', 'Not a member of this pack');
    END IF;
    
    -- Create post
    INSERT INTO wolfpack_posts (
        author_id,
        pack_id,
        content,
        post_type,
        media_urls
    ) VALUES (
        v_user_id,
        p_pack_id,
        p_content,
        p_post_type,
        p_media_urls
    ) RETURNING id INTO v_video_id;
    
    -- Log activity
    PERFORM log_pack_activity(
        p_pack_id,
        v_user_id,
        'post_created',
        jsonb_build_object('video_id', v_video_id)
    );
    
    RETURN jsonb_build_object(
        'success', true,
        'video_id', v_video_id,
        'message', 'Post created successfully!'
    );
END;
$$;


