CREATE OR REPLACE FUNCTION "public"."get_analytics_overview"() RETURNS "json"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'pg_catalog', 'public'
    AS $$
DECLARE
    v_result json;
BEGIN
    -- Simple check using check_is_admin
    IF NOT check_is_admin(auth.uid()) THEN
        RETURN json_build_object(
            'success', false,
            'error', 'Admin access required'
        );
    END IF;
    
    SELECT json_build_object(
        'success', true,
        'data', json_build_object(
            'overview', row_to_json(ao.*),
            'charts', json_build_object(
                'user_growth', (
                    SELECT json_agg(json_build_object(
                        'date', date,
                        'count', count
                    ) ORDER BY date)
                    FROM (
                        SELECT 
                            DATE(created_at) as date,
                            COUNT(*) as count
                        FROM users
                        WHERE created_at > NOW() - INTERVAL '30 days'
                        GROUP BY DATE(created_at)
                    ) ug
                ),
                'message_activity', (
                    SELECT json_agg(json_build_object(
                        'date', date,
                        'count', count
                    ) ORDER BY date)
                    FROM (
                        SELECT 
                            DATE(created_at) as date,
                            COUNT(*) as count
                        FROM wolf_chat
                        WHERE created_at > NOW() - INTERVAL '7 days'
                        GROUP BY DATE(created_at)
                    ) ma
                )
            )
        )
    ) INTO v_result
    FROM analytics_overview ao;
    
    RETURN v_result;
END;
$$;


