        'created_at', v.created_at,
        'is_liked', EXISTS(
            SELECT 1 FROM wolfpack_post_likes 
            WHERE video_id = v.id AND user_id = p_user_id
        ),
        'user', jsonb_build_object(
            'id', u.id,
