    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public', 'pg_catalog'
    AS $$
DECLARE
    v_video JSONB;
BEGIN
    SELECT jsonb_build_object(
        'id', v.id,
        'user_id', v.user_id,
        'video_url', v.video_url,
        'thumbnail_url', v.thumbnail_url,
