CREATE OR REPLACE FUNCTION "public"."log_admin_operation"("p_operation_type" "text", "p_function_name" "text", "p_target_table" "text" DEFAULT NULL::"text", "p_target_id" "uuid" DEFAULT NULL::"uuid", "p_details" "jsonb" DEFAULT '{}'::"jsonb", "p_success" boolean DEFAULT true, "p_error_message" "text" DEFAULT NULL::"text") RETURNS "void"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'pg_catalog', 'public'
    AS $$
DECLARE
    v_user_email text;
    v_user_role text;
    v_headers json;
BEGIN
    -- Get user details
    SELECT email, role INTO v_user_email, v_user_role 
    FROM public.users 
    WHERE auth_id = auth.uid();
    
    -- Get request headers safely
    BEGIN
        v_headers := current_setting('request.headers', true)::json;
    EXCEPTION WHEN OTHERS THEN
        v_headers := '{}'::json;
    END;
    
    -- Insert audit log
    INSERT INTO public.admin_operations_log (
        operation_type,
        function_name,
        user_id,
        user_email,
        user_role,
        target_table,
        target_id,
        operation_details,
        ip_address,
        user_agent,
        success,
        error_message
    ) VALUES (
        p_operation_type,
        p_function_name,
        auth.uid(),
        v_user_email,
        v_user_role,
        p_target_table,
        p_target_id,
        p_details,
        v_headers->>'x-forwarded-for',
        v_headers->>'user-agent',
        p_success,
        p_error_message
    );
END;
$$;


