    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'public'
    AS $$
DECLARE
    v_content_id uuid;
    v_user_id uuid;
BEGIN
    -- Get user_id from job
    SELECT created_by INTO v_user_id
    FROM wolfpack_ingestion_jobs
    WHERE id = p_job_id;
    
    -- Insert ingested content
    INSERT INTO wolfpack_ingested_content (
        job_id,
        platform,
