        p_title,
        p_description,
        p_media_url,
        p_thumbnail_url,
        p_author_name,
        p_author_id,
        p_metadata,
        now()
    ) RETURNING id INTO v_content_id;
    
    -- Create a wolfpack_videos entry
    INSERT INTO wolfpack_videos (
        user_id,
        video_url,
        thumbnail_url,
