CREATE OR REPLACE FUNCTION "public"."single_ladies_spotlight"("p_dj_id" "uuid", "p_location_id" "uuid", "p_custom_message" "text" DEFAULT NULL::"text") RETURNS "uuid"
    LANGUAGE "plpgsql" SECURITY DEFINER
    SET "search_path" TO 'pg_catalog', 'public'
    AS $$
DECLARE
    v_broadcast_id UUID;
    v_message TEXT;
BEGIN
    v_message := COALESCE(
        p_custom_message, 
        'üíÉ All the single ladies, make some noise! This one''s for you! Get on the dance floor and show us what you got! üíú'
    );
    
    INSERT INTO dj_broadcasts (
        dj_id,
        location_id,
        broadcast_type,
        title,
        message,
        priority,
        duration_seconds,
        auto_close,
        status,
        background_color,
        text_color,
        accent_color,
        animation_type,
        emoji_burst,
        interaction_config
    ) VALUES (
        p_dj_id,
        p_location_id,
        'spotlight',
        'üíÉ SINGLE LADIES SPOTLIGHT!',
        v_message,
        'urgent',
        45,
        true,
        'active',
        '#ec4899',
        '#ffffff',
        '#fbbf24',
        'bounce',
        ARRAY['üíÉ', 'üíú', '‚ú®', 'üî•', 'üëë'],
        json_build_object(
            'response_type', 'emoji',
            'show_results_live', true,
            'anonymous_responses', false
        )
    ) RETURNING id INTO v_broadcast_id;
    
    -- Update sent_at and expires_at
    UPDATE dj_broadcasts 
    SET 
        sent_at = NOW(),
        expires_at = NOW() + INTERVAL '45 seconds'
    WHERE id = v_broadcast_id;
    
    RETURN v_broadcast_id;
