    -- Check if user is an active wolfpack member
    IF NOT EXISTS (
        SELECT 1 FROM public.users 
        WHERE id = NEW.user_id 
        AND wolfpack_status = 'active'
    ) THEN
        RAISE EXCEPTION 'Only active Wolfpack members can open bar tabs';
    END IF;
    
    RETURN NEW;
END;
$$;


