CREATE OR REPLACE VIEW "public"."active_broadcasts_base" WITH ("security_invoker"='on') AS
 SELECT "db"."id",
    "db"."dj_id",
    "db"."location_id",
    "db"."message",
    "db"."broadcast_type",
    "db"."created_at",
    "db"."session_id",
    "db"."title",
    "db"."subtitle",
    "db"."background_color",
    "db"."text_color",
    "db"."accent_color",
    "db"."animation_type",
    "db"."emoji_burst",
    "db"."interaction_config",
    "db"."duration_seconds",
    "db"."auto_close",
    "db"."priority",
    "db"."status",
    "db"."sent_at",
    "db"."expires_at",
    "db"."closed_at",
    "db"."view_count",
    "db"."interaction_count",
    "db"."unique_participants",
    "db"."tags",
    "db"."category",
    "db"."updated_at",
        CASE
            WHEN ("db"."expires_at" IS NOT NULL) THEN (GREATEST((0)::numeric, EXTRACT(epoch FROM ("db"."expires_at" - "now"()))))::integer
            ELSE NULL::integer
        END AS "seconds_remaining"
   FROM "public"."dj_broadcasts" "db"
  WHERE ("db"."status" = 'active'::"text");


ALTER TABLE "public"."active_broadcasts_base" OWNER TO "postgres";


CREATE OR REPLACE VIEW "public"."active_broadcasts_live" WITH ("security_invoker"='on') AS
 SELECT "db"."id",
    "db"."dj_id",
    "db"."location_id",
    "db"."message",
    "db"."broadcast_type",
    "db"."created_at",
    "db"."session_id",
    "db"."title",
    "db"."subtitle",
    "db"."background_color",
    "db"."text_color",
    "db"."accent_color",
    "db"."animation_type",
    "db"."emoji_burst",
    "db"."interaction_config",
    "db"."duration_seconds",
    "db"."auto_close",
    "db"."priority",
    "db"."status",
    "db"."sent_at",
    "db"."expires_at",
    "db"."closed_at",
    "db"."view_count",
    "db"."interaction_count",
    "db"."unique_participants",
    "db"."tags",
    "db"."category",
    "db"."updated_at",
        CASE
            WHEN ("db"."expires_at" IS NOT NULL) THEN (GREATEST((0)::numeric, EXTRACT(epoch FROM ("db"."expires_at" - "now"()))))::integer
            ELSE NULL::integer
        END AS "seconds_remaining",
    "u"."display_name" AS "dj_name",
    "u"."profile_image_url" AS "dj_avatar"
   FROM ("public"."dj_broadcasts" "db"
     JOIN "public"."users" "u" ON (("db"."dj_id" = "u"."id")))
  WHERE (("db"."status" = 'active'::"text") AND (("db"."expires_at" IS NULL) OR ("db"."expires_at" > "now"())));


