    CONSTRAINT "notifications_type_check" CHECK (("type" = ANY (ARRAY['info'::"text", 'warning'::"text", 'error'::"text", 'order_new'::"text", 'order_ready'::"text", 'follow'::"text", 'like'::"text", 'comment'::"text", 'video_like'::"text", 'video_comment'::"text"])))
);


